import React, { Component } from 'react';
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import aktifLogo from '../../src/slide-logos/aktifbank-logo.png';
import boschLogo from '../../src/slide-logos/bosch-logo.png';
import akbankLogo from '../../src/slide-logos/akbank-logo.png';
import arcelikLogo from '../../src/slide-logos/arcelik-logo.png';
import allianzLogo from '../../src/slide-logos/allianz-logo.png';

const responsive = {
  superLargeDesktop: {
    // the naming can be any, depends on you.
    breakpoint: { max: 4000, min: 3000 },
    items: 8
  },
  desktop: {
    breakpoint: { max: 3000, min: 1024 },
    items: 7
  },
  tablet: {
    breakpoint: { max: 1024, min: 464 },
    items: 2
  },
  mobile: {
    breakpoint: { max: 464, min: 0 },
    items: 1
  }
};

class DemoCarousel extends Component {
    render() {
        return (
        <Carousel responsive={responsive} swipeable={false}
        draggable={false}
        showDots={true}
        ssr={true} // means to render carousel on server-side.
        infinite={true}
        autoPlay={this.props.deviceType !== "mobile" ? true : false}
        autoPlaySpeed={1000}
        keyBoardControl={true}
        customTransition="all .15"
        transitionDuration={500}
        containerClass="carousel-container"
        removeArrowOnDeviceType={["tablet", "mobile"]}
        deviceType={this.props.deviceType}
        dotListClass="custom-dot-list-style"
        itemClass="carousel-item-padding-40-px">
          <div className="slideItems">
            <div className="slideItems__title">
              Front-end Dev…
            </div> 
            <div className="slideItems__title">
              Bosch
            </div>
            <div className="slideItems__title">
            <img src={aktifLogo} className="" alt="Aktifbank Logo" />
            </div> 
          </div>
          <div className="slideItems">
            <div className="slideItems__title">
              Front-end Dev…
            </div> 
            <div className="slideItems__title">
              Bosch
            </div>
            <div className="slideItems__title">
            <img src={boschLogo} className="" alt="Bosch Logo" />
            </div> 
          </div>
          <div className="slideItems">
            <div className="slideItems__title">
              Front-end Dev…
            </div> 
            <div className="slideItems__title">
              Bosch
            </div>
            <div className="slideItems__title">
            <img src={akbankLogo} className="" alt="Akbank Logo" />
            </div> 
          </div>
          <div className="slideItems">
            <div className="slideItems__title">
              Front-end Dev…
            </div> 
            <div className="slideItems__title">
              Bosch
            </div>
            <div className="slideItems__title">
            <img src={arcelikLogo} className="" alt="Arçelik Logo" />
            </div> 
          </div>
          <div className="slideItems">
            <div className="slideItems__title">
              Front-end Dev…
            </div> 
            <div className="slideItems__title">
              Bosch
            </div>
            <div className="slideItems__title">
            <img src={allianzLogo} className="" alt="Allianz Logo" />
            </div> 
          </div>



        </Carousel>
      );
    }
};

export default DemoCarousel;



